import UIKit
import AppNetwork

public enum SignInDependencies {
    public static var signInProvider: AuthenticationProviderProtocol!
}

final class SignInInteractor {
    var isValidEmail = false
    var isValidPassword = false
    weak var presenter: SignInPresenterProtocol?
}

extension SignInInteractor: SignInInteractorProtocol {
    func loginUser(email: String, password: String) {
        SignInDependencies.signInProvider.loginUser(
            withEmail: email,
            password: password,
            success: {
                print("logged in")
                self.presenter?.loginSuccessful()
            },
            failure: { error in
                self.presenter?.loginError(error)
            }
        )
    }
    
    
}
